
public class CastingEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// (기본형) 캐스팅 연산자 -> 형변환
		// 변수 또는 상수의 타입을 다른 타입으로 변환하는 것
		
		double d = 85.4;
		float score = (int)d;
		
		System.out.println("score = " + score);
		System.out.println("d= " + d);
		
		

	}

}
