
public class CastingExTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n = 0;
		double doubleNum = 17.131;

		n = (int)doubleNum;
		
		System.out.println("n = " + n); // n = 17.131을 프린트한다
		
		doubleNum = (double)n;
				
		System.out.println("doubleNum = " + doubleNum);
		
				
		
	}

}
