
public class OperEx10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		논리연산자
//		| => or 연산자, & => and 연산자
		int n = 0;
		
		n = 10;
//		10 / 2 == 5
		System.out.println(n % 2 == 0 || n % 3 == 0);
//						ㄴ사칙연산 먼저, 비교연산 다음, 논리연산
		System.out.println(n % 2 == 0 && n % 3 == 0);
		
		
		
		
	}

}
